    private void loginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_loginActionPerformed
        // TODO add your handling code here:
        int select = account.getSelectedIndex();
        String pass = String.valueOf(password.getPassword());
        if(select == 0){
            JOptionPane.showMessageDialog(this,"You haven't selected any Account.");
            password.setText("");
        }
        else if(pass.equals("")){
            JOptionPane.showMessageDialog(this,"Enter Password First.");
        }
        else{
            String name_s = account.getItemAt(account.getSelectedIndex());
            if(("User".equals(name_s)) && ("user123".equals(pass))){
                JOptionPane.showMessageDialog(this,"Login is Successful. \n\nWelcome User.");
                new DataEntryForm().setVisible(true);
                dispose();
            }
            else if(("Admin".equals(name_s)) && ("admin123".equals(pass))){
                JOptionPane.showMessageDialog(this,"Login is Successful. \n\nWelcome Admin.");
                //new regform.AdminHome().setVisible(true);
                dispose();
            }
            else{ 
                JOptionPane.showMessageDialog(this,"Wrong password.", "Failure", JOptionPane.ERROR_MESSAGE);                
                password.setText("");
            }
        }        
    }//GEN-LAST:event_loginActionPerformed

